import { useState, useEffect } from 'react'
import axios from 'axios'


const App = () => {
  const [country, setCountry] = useState([])
  const [newCountry, setNewCountry] = useState('')
  var Countries = []
  useEffect(() => {
    axios
      .get('https://restcountries.com/v3.1/all')
      .then(response => {
        console.log('promise fulfilled')
        Countries = response.data
      })
  }, []) 


  const searchCountry = (event) => {
    event.preventDefault()
    setNewCountry(event.target.value)
    setCountry(Countries.filter(country => country.name.common.includes(event.target.value)))
  }

  const Info = ({country}) => {
    if(Countries.lenght > 10){
      return <p>Too many mathces, specify another filter</p>
    }
    else if(Countries.length != 1){
      return Countries.map(country => <p id={country.name.common}>{country.name.common}</p>)
    }
    else{
      return <p>epic</p>
    }
    country.map(country )
  }
  return (
    <div>
      find countries <input value={newCountry} onChange={searchCountry}/>
      <Info country = {country} />
    </div>
  )
}

export default App
