import { useState, useEffect } from 'react'
import axios from 'axios'


const App = () => {
  const [country, setCountry] = useState([])
  const [newCountry, setNewCountry] = useState('')
  const [list, setList] = useState([])

  useEffect(() => {
    axios
      .get('https://restcountries.com/v3.1/all')
      .then(response => {
        console.log('promise fulfilled')
        setList(response.data)
      })
  }, []) 


  const searchCountry = (event) => {
    event.preventDefault()
    setNewCountry(event.target.value)
    setCountry(list.filter(country => country.name.common.includes(event.target.value)))
    console.log("fulfilled")
  }

  const Info = ({country}) => {
    console.log(list)
    console.log(country)
    if(country.length > 10){
      return <p>Too many matches, specify another filter</p>
    }
    else if(country.length !== 1){
      return country.map(x => <p key={x.name.common}>{x.name.common}</p>)
    }
    else{
      country = country[0]
      return(
        <div>
          <div>
            <h1>{country.name.common}</h1>
          </div>
          <div>
            Capital: {country.capital[0]}
          </div>
          <div>
            Area: {country.area} units
          </div>
        </div>
      )
    }
  }
  return (
    <div>
      find countries <input value={newCountry} onChange={searchCountry}/>
      <Info country = {country} />
    </div>
  )
}

export default App
