import { useState, useEffect } from 'react'
import axios from 'axios'


const App = () => {
  const [country, setCountry] = useState([])
  const [newCountry, setNewCountry] = useState('')
  var Countries = []

  useEffect(() => {
    axios
      .get('https://restcountries.com/v3.1/all')
      .then(response => {
        console.log('promise fulfilled')
        Countries = response.data
      })
  }, []) 


  const searchCountry = (event) => {
    event.preventDefault()
    setNewCountry(event.target.value)
    const countriesCopy = Countries.filter(country => country.name.common.includes(event.target.value))
    setCountry(countriesCopy)
    console.log("fulfilled")
  }

  const Info = ({country}) => {
    console.log(Countries)
    console.log(country)
    if(country.length > 10){
      return <p>Too many matches, specify another filter</p>
    }
    else if(country.length !== 1){
      return country.map(x => <p id={x.name.common}>{x.name.common}</p>)
    }
    else{
      return <p>epic</p>
    }
  }
  return (
    <div>
      find countries <input value={newCountry} onChange={searchCountry}/>
      <Info country = {country} />
    </div>
  )
}

export default App
