const onDelete = (id) => {
  if(window.confirm('Would you like to delete this entry?')){
    personServices.remove(id)
    const personCopy = (persons.filter(person => person.id !== id))
    setPersons(personCopy)
    setNewName('')
    setNewNumber('')
    setNewFilter('')
    setShown(personCopy)
  }
  else{
    setNewName('')
    setNewNumber('')
    setNewFilter('')
  }
}
const Phonebook = ({shown}) => 
  shown.map(person => <p key = {person.id}>{person.name} {person.number} <button onClick={() => onDelete(person.id)}>Delete</button> </p>)
