import { useState, useEffect } from 'react'
import axios from 'axios'
import Phonebook from './components/Phonebook.js'
import Filter from './components/Filter.js'
import SubmitField from './components/SubmitField.js'

const App = () => {
  const [persons, setPersons] = useState([])
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [filter, setNewFilter] = useState('')
  const [shown, setShown] = useState([])

  const addToBook = (event) => {
    event.preventDefault()
    if(persons.some(person => person.name === newName)){
      alert(`${newName} is already in the phonebook`)
      setNewName('')
    }
    else{
      const newPerson = {
        name: newName,
        number: newNumber,
        id: persons.length + 1
      }
      const personCopy = persons.concat(newPerson)
      setPersons(personCopy)
      setNewName('')
      setNewNumber('')
      setNewFilter('')
      setShown(personCopy)
      console.log(persons)
    }
  }

  const nameChange = (event) => {
    console.log(event.target.value)
    setNewName(event.target.value)
  }

  const numberChange = (event) => {
    console.log(event.target.value)
    setNewNumber(event.target.value)
  }

  const filterChange = (event) => {
    const substring = event.target.value
    console.log(substring)
    setNewFilter(substring)
    const shownCopy = (substring === '') ? persons : persons.filter(person => person.name.toLowerCase().includes(substring))
    setShown(shownCopy)
  }



  useEffect(() => {
    axios
      .get('http://localhost:3001/persons')
      .then(response => {
        console.log('promise fulfilled')
        setShown(response.data)
        setPersons(response.data)
      })
  }, [])


  return (
    <div>
      <h2>Phonebook</h2>
      <Filter value={filter} onChange={filterChange} />
      <h2>add a number</h2>
      <form onSubmit={addToBook}>
        <div>
          name:  <SubmitField value={newName} onChange={nameChange} />
        </div>
        <div>
          number: <SubmitField value={newNumber} onChange={numberChange} />
        </div>
        <div>
          <button  type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      <div>
        <Phonebook shown={shown} />
      </div>
    </div>
  )
}

export default App
