import { useState } from 'react'


const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', number: '040-123456', id: 1 },
    { name: 'Ada Lovelace', number: '39-44-5323523', id: 2 },
    { name: 'Dan Abramov', number: '12-43-234345', id: 3 },
    { name: 'Mary Poppendieck', number: '39-23-6423122', id: 4 }
  ])
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [filter, setFilter] = useState('')
  const [numbersShown, setNumbers] = useState(persons)

  const addToBook = (event) => {
    event.preventDefault()
    if(persons.some(person => person.name === newName)){
      alert(`${newName} is already in the phonebook`)
      setNewName('')
    }
    else{
      const newPerson = {
        name: newName,
        number: newNumber,
        id: persons.length + 1
      }
      setPersons(persons.concat(newPerson))
      const updateShown = (sift === '') ? persons : persons.filter(person => person.name.toLowerCase().includes(sift))
      setNumbers(updateShown)
      setNewName('')
      setNewNumber('')
      console.log(persons)
    }
  }

  const nameChange = (event) => {
    console.log(event.target.value)
    setNewName(event.target.value)
  }

  const numberChange = (event) => {
    console.log(event.target.value)
    setNewNumber(event.target.value)
  }

  const filterChange = (event) => {
    const sift = event.target.value
    setFilter(sift)
    const updateShown = (sift === '') ? persons : persons.filter(person => person.name.toLowerCase().includes(sift))
    setNumbers(updateShown)
  }


  return (
    <div>
      <h2>Phonebook</h2>
      <form>
        filter shown with<input value={filter} onChange={filterChange}/>
      </form>
      <h2>add a number</h2>
      <form onSubmit={addToBook}>
        <div>
          name:   <input value={newName} onChange={nameChange}/>
        </div>
        <div>
          number: <input value={newNumber} onChange={numberChange}/>
        </div>
        <div>
          <button  type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      <div>
        {numbersShown.map(person => <p key = {person.id}>{person.name} {person.number}</p>)}
      </div>
    </div>
  )
}

export default App
